import React, { useState } from 'react';
import { Paper, Tabs, Tab } from "@material-ui/core";
import { Home, Announcement, Face } from '@material-ui/icons/';
import { withRouter } from "react-router-dom";

const DashboardDrawer = props => {
  const [ value, setValue ] = useState(0);
  const handleChange = (event, newValue) => {
    setValue(newValue);
    switch (newValue) {
      case 0: return props.history.push("/dashboard");
      case 1: return props.history.push("/dashboard/announcements");
      case 2: return props.history.push("/dashboard/profile");
    }
  }
  return (
    <Paper style={{ height: window.innerHeight, marginTop: 60, position: "fixed", width: 100 }}>
      <Tabs
        value={value}
        style ={{ minWidth: 100 }}
        orientation="vertical"
        onChange={handleChange}
        variant="fullWidth"
        textColor="primary"
      >
        <Tab icon={<Home style = {{ fontSize: 40, padding: 0, minWidth: "100%" }} />} />
        <Tab icon={<Announcement />} />
        <Tab icon={<Face />} />
      </Tabs>
    </Paper>
  );
};


export default withRouter(DashboardDrawer);
