import React, {useState} from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  DialogContentText,
  Grid
} from "@material-ui/core";
import { createMuiTheme } from "@material-ui/core/styles";
import { green } from "@material-ui/core/colors";
import Select from "react-select";

const StudentAddMarksDialog = props => {
  const { isMarksDialogOpen, isMarksEdit, addOrEditMarksOpenHandler } = props;
  const subjectLists = [{ value: "physics", label: "Physics" }, { value: "chemistry", label: "Chemistry" }, { value: "maths", label: "Mathematics" }];
  const [ selectedSubject, setSelectedSubject ] = useState("")
  return (
    <Dialog open={isMarksDialogOpen} onClose={() => addOrEditMarksOpenHandler(isMarksEdit, !isMarksDialogOpen)} aria-labelledby="form-dialog-title">
      <DialogTitle>{isMarksEdit ? "Edit Marks" : "Add Marks"}</DialogTitle>
      <DialogContent>
        <DialogContentText>
          To subscribe to this website, please enter your email address here. We will send updates
          occasionally.
        </DialogContentText>
        <Select options={subjectLists} defaultValue={selectedSubject} onChange={selectedVal => setSelectedSubject(selectedVal.value)} />
        <TextField
          autoFocus
          margin="dense"
          id="name"
          label="Email Address"
          type="email"
          fullWidth
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={() => addOrEditMarksOpenHandler(isMarksEdit, !isMarksDialogOpen)} color="primary">
          Cancel
        </Button>
        <Button onClick={() => addOrEditMarksOpenHandler(isMarksEdit, !isMarksDialogOpen)} color="primary">
          Subscribe
        </Button>
      </DialogActions>
    </Dialog>
  );
}

export default StudentAddMarksDialog;
