import React, {useEffect, useState} from 'react';
import { Button, Grid, TextField, Typography } from "@material-ui/core";
import useGetDepartments from "../../../remoteHooks/getDepartments";
import Select from "react-select";

const BatchContainer = props => {
  const [ departments, loading, error ] = useGetDepartments();
  const [ allDepartments, setAllDepartments ] = useState([]);
  const [ departmentId, setDepartmentId ] = useState("");
  const [ batchName, setBatchName ] = useState("");
  useEffect(() => {
    if (!loading) {
      // if (!error) {
        console.log(departments);
        setAllDepartments(departments.map(department => ({
          label: department.departmentName,
          value: department.id
        })));
      // }
    }
  }, [departments]);
  return (
    <Grid container justify={"center"} spacing={0}>
      <Grid item xs={12}>
        <Grid container justify={"center"} style={{ paddingTop: "8px" }}>
          <Grid item xs={6} style={{ textAlign: "center"}}>
            <Typography variant={"h4"} style={{ fontWeight: 300 }}>
              Add New Batch
            </Typography>
          </Grid>
        </Grid>
        <Grid container justify={"center"} style={{ marginTop: 16 }}>
          <Grid item xs={6}>
            <Select
              options={allDepartments}
              placeholder={"Choose Department"}
              onChange={selectedVal => setDepartmentId(selectedVal.value)}
            />
            {/*<TextField*/}
            {/*  label="Department Name"*/}
            {/*  variant="outlined"*/}
            {/*  autoFocus={true}*/}
            {/*  value={departmentName}*/}
            {/*  onChange={event => setDepartmentName(event.target.value)}*/}
            {/*  fullWidth*/}
            {/*/>*/}
            <TextField
              style={{ marginTop: "12px" }}
              label="Batch Name"
              variant="outlined"
              value={batchName}
              onChange={event => setBatchName(event.target.value)}
              fullWidth
            />
            <Grid container justify={"flex-end"}>
              <Grid item xs={3}>
                <Button
                  variant={"contained"}
                  color={"secondary"}
                  style={{ marginTop: "16px", marginBottom: "16px" }}
                  onClick={() => {}}
                  fullWidth
                >
                  Save
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
}

export default BatchContainer;
