import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Paper, Tabs, Tab } from "@material-ui/core";
import { Apps, Announcement } from '@material-ui/icons/';
import { indigo } from "@material-ui/core/colors";
import { AccessTime, NotificationsActive, Face } from "@material-ui/icons";
import { withRouter } from "react-router-dom";

const drawerWidth = 230;
const DashboardDrawer = props => {
  const classes = useStyles();
  const [ value, setValue ] = useState(0);
  const handleChange = (event, newValue) => {
    setValue(newValue);
    switch (newValue) {
      case 0: return props.history.push("/dashboard");
      case 1: return props.history.push("/dashboard/announcements");
      case 2: return props.history.push("/dashboard/profile");
    }
  }
  return (
    <Paper square className={classes.root}>
      <Tabs
        value={value}
        orientation="vertical"
        onChange={handleChange}
        variant="fullWidth"
        indicatorColor="primary"
        textColor="primary"
      >
        <Tab icon={<Apps />} />
        <Tab icon={<Announcement />} />
        <Tab icon={<PersonPinIcon />} />
      </Tabs>
    </Paper>
  );
};


export default withRouter(DashboardDrawer);
