import React, { useState } from 'react';
import {Table, TableHead, TableRow, TableCell, TableBody, Button, Paper, Typography} from "@material-ui/core";
import {createMuiTheme, ThemeProvider} from "@material-ui/core/styles";
import AddFacultyAttendanceDialog from "./Dialogs/AddFacultyAttendanceDialog";
import {green, red} from "@material-ui/core/colors";
import DateFnsUtils from "@date-io/date-fns";
import {MuiPickersUtilsProvider} from "@material-ui/pickers";

const FacultyTable = props => {
  const tableHeaders = ["Faculty Name", "Department", "Working Days", "Actions"];
  const [ isOpenAttendanceDialog, setOpenAttendanceDialog ] = useState(false);
  const [ selectedFaculty, setSelectedFaculty ] = useState({});
  const { faculties } = props;
  const markFacultyAbsent = (facultyId) => {

  }
  const markFacultyAttendance = (facultyId, type, time, attendanceId) => {
    if (type === "exit") {

    } else {

    }
  }
  return (
    <MuiPickersUtilsProvider utils={DateFnsUtils}>
      <Paper elevation={3}>
        <Table size={'medium'}>
          <TableHead>
            <TableRow>
              {tableHeaders.map((tableHeader, index) => (
                <TableCell
                  key={index}
                  align={'left'}
                  padding={'default'}
                  sortDirection={false}
                >
                  {tableHeader}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {faculties.map(faculty => (
              <TableRow key={faculty.id}>
                <TableCell component="th" scope="row">
                  {faculty.fullName}
                </TableCell>
                <TableCell>{faculty.department.departmentName}</TableCell>
                <TableCell>{faculty.workingDays}</TableCell>
                <TableCell>
                  {faculty.attendance.length === 0 ?
                    <ThemeProvider theme={theme}>
                      <Button variant="contained" color={faculty.attendance.length > 0 ? "primary" : "secondary"} style={{ color: "white" }} onClick={() => markFacultyAbsent(faculty.id)} style={{ marginRight: 16 }}>
                        Mark Leave
                      </Button>
                      <Button variant="contained" color={faculty.attendance.length > 0 ? "secondary" : "primary"} style={{ color: "white" }} onClick={() => setOpenAttendanceDialog(true)}>
                        Entry Attendance
                      </Button>
                    </ThemeProvider>
                    :
                    <ThemeProvider theme={theme}>
                      {!faculty.attendance[0].isPresent && <Typography variant={"body2"}>Faculty on Leave</Typography>}
                      {faculty.attendance[0].isPresent && <Button variant="contained" color={faculty.attendance.length > 0 ? "secondary" : "primary"} style={{ color: "white" }} onClick={() => setOpenAttendanceDialog(true)} style={{ marginRight: 16 }}>
                        {faculty.attendance.length > 0 ? "Exit Attendance" : "Entry Attendance" }
                      </Button>}
                    </ThemeProvider>
                  }
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <AddFacultyAttendanceDialog isOpenAttendanceDialog={isOpenAttendanceDialog} setOpenAttendanceDialog={setOpenAttendanceDialog} facultyAttendance={selectedFaculty.attendance} markFacultyAttendance={markFacultyAttendance} />
      </Paper>
    </MuiPickersUtilsProvider>
  );
}

const theme = createMuiTheme({
  palette: {
    primary: green,
    secondary: red
  }
});

export default FacultyTable;
