import React, {useEffect, useState} from 'react';
import {getStudents} from "../remoteMethods/Student/student";

const useGetStudents = (fullName, departmentId, semester) => {
  const [ students, setStudents ] = useState([]);
  const [ startSearch, setStartSearch ] = useState(false);
  useEffect(() => {
    if (startSearch) {
      getStudents({ fullName: { like: fullName }, departmentId, semester }).then(students => {
        setStartSearch(false);
        setStudents(students);
      }).catch(error => {
        setStartSearch(false);
        setStudents([]);
      })
    }
  }, [startSearch]);
  return [ students, setStartSearch];
}

export default useGetStudents;
